import React, { useState } from 'react';
import '../App.css';
import data from '../india.min.geo.json';
import GeoChart from '../components/GeoChart';
import { Card, Carousel, Col, Container, Figure, Row } from 'react-bootstrap';
import { FaArrowUp } from 'react-icons/fa';

function MapPage() {
  const [property, setProperty] = useState('');

  return (
    <React.Fragment>
      <Container className='main-container' fluid>
        <Row style={{ height: '90%' }}>
          {/* col-left */}
          <Col>
            <Row>
              <Col>
                <Figure>
                  <Figure.Image
                    width={300}
                    height={300}
                    alt='300x300'
                    src='https://i.pinimg.com/originals/64/a5/92/64a5923a87f1a724a0b2f5ea1535dcab.png'
                  />
                </Figure>
              </Col>
            </Row>
            <Row>
              <Col>
                <Figure>
                  <Figure.Image
                    width={300}
                    height={300}
                    alt='300x300'
                    src='https://i.pinimg.com/originals/64/a5/92/64a5923a87f1a724a0b2f5ea1535dcab.png'
                  />
                </Figure>
              </Col>
            </Row>
          </Col>

          {/* col-middle */}
          <Col
            xs
            lg={6}
            style={{
              display: 'flex',
              justifyContent: 'center',
            }}
          >
            <GeoChart data={data} />
          </Col>

          {/* col-right */}
          <Col style={{ height: '100%' }}>
            <Row style={{ height: '100%' }}>
              <Col style={{ height: '100%' }} className='right-container'>
                <Card
                  bg='light'
                  border='light'
                  style={{ width: '90%' }}
                  className='mb-2 right-container__top'
                >
                  <Card.Body>
                    <Card.Img
                      variant='top'
                      src='/pie-chart.png'
                      style={{ height: '120px' }}
                    />
                  </Card.Body>
                </Card>
                <Card
                  bg='light'
                  style={{ width: '90%' }}
                  className='mb-2 right-container__bottom'
                  border='light'
                >
                  <Card.Body>
                    <Card.Img variant='top' src='/pie-chart.png' />

                    <Card.Title>Total Count </Card.Title>
                    <Card.Text>
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Col>
        </Row>
      </Container>
      {/* <h2>India Map with d3-geo</h2>
      <GeoChart data={data} property={property} /> */}
      {/* <h2>Select property to highlight</h2>
      <select
        value={property}
        onChange={(event) => setProperty(event.target.value)}
      >
        <option value='pop_est'>Population</option>
        <option value='name_len'>Name length</option>
        <option value='gdp_md_est'>GDP</option>
      </select> */}
      {/* <Video /> */}
    </React.Fragment>
  );
}

export default MapPage;
