import React, { useState, useEffect, useRef } from 'react';
import useResizeObserver from '../../../hooks/useResizeObserver';
import {
  select,
  line,
  scaleLinear,
  aaxisBottom,
  axisLeft,
  axisBottom,
} from 'd3';
// const d3 = require('d3');

function LineChart({ data }) {
  const wrapperRef = useRef();
  const svgRef = useRef();
  const dimensions = useResizeObserver(wrapperRef);
  //   const [indiaData, setIndiaData] = useState([]);

  //   const data = [10, 20, 30, 40, 50];

  useEffect(() => {
    // console.log(data);

    if (!data) return;

    if (!data[0]) return;

    const yearsArray = Object.keys(data[0].Data);

    const indiaData = yearsArray.map((year) => {
      return { key: year, value: data[0].Data[year] };
    });

    const svg = select(svgRef.current);

    const xScale = scaleLinear()
      .domain((data) => data.map((val, idx) => val.key))
      .range([0, 150]);

    const xAxis = axisBottom(xScale).ticks(data.length);
    svg.select('.x-axis').call(xAxis);

    const yScale = scaleLinear().domain([0, 150]).range([150, 0]);

    // const myLine = line().x().y();
    const myLine = line()
      .x((value, idx) => value.key)
      .y((value) => value.value['Total Forest Cover Area']);

    // svg.selectAll('path').data([data]).join('path');

    svg
      .selectAll('path')
      .data(indiaData)
      .join('path')
      .attr('d', (value) => myLine(value));
    //   .data([data])
    //   .join('path')
    //
    // svg.selectAll('path').data(data[0].Data);
  }, [data]);

  return (
    <div ref={wrapperRef}>
      <svg ref={svgRef}></svg>
    </div>
  );
}

export default LineChart;
