import React from 'react';
import { Chart } from 'react-google-charts';

function GaugeChart({ aqiParams }) {
  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        width: '100%',
        flexDirection: 'column',
      }}
    >
      {aqiParams.length !== 0 && (
        <>
          <div className='aqi-param-selector'>
            <select>
              {aqiParams.map((arr) => (
                <option>{arr[0]}</option>
              ))}
            </select>
          </div>

          <Chart
            width={'100%'}
            height={'100%'}
            chartType='Gauge'
            loader={<div>Loading Chart</div>}
            data={[
              ['AQI Param', '%Value'],
              ['SO2', aqiParams[1] / 10000],
            ]}
            options={{
              redFrom: 90,
              redTo: 100,
              yellowFrom: 75,
              yellowTo: 90,
              minorTicks: 5,
            }}
            rootProps={{ 'data-testid': '1' }}
          />
        </>
      )}
    </div>
  );
}

export default GaugeChart;
